mapper.update:
UPDATE tb_material_history
SET  history_comment = #{historyComment}
    ,upd_name= #{updName}
    ,upd_date= #{updDate}
    ,upd_id = #{userId}
    ,upd_dt = getdate()
where  history_id = #{historyId}

mapper.select:
select count(item_cd)
from   tb_material_mapping
where  item_cd = #{itemCd}
