mapper.select:
select b.contract_date +' -'+ CAST(b.seq AS NVARCHAR) AS contractDateSeq ,a.po_no ,a.item_cd ,c.item_name ,b.expected_due_date ,d.client_name ,e.member_nm as managerName ,a.qty ,a.unit_price ,a.supply_price ,a.vat_price ,isnull(a.supply_price, 0)+ isnull(a.vat_price, 0) as totPrice ,dbo.getCommonName('order_type', b.order_type) as orderType ,dbo.getCommonName('status_Type',a.status_type) as statusType ,b.payment_condition ,b.contract_id ,b.contract_date from tb_contract_item a inner join tb_contract_mst b on b.contract_id = a.contract_id inner join tb_item_mst c on c.item_cd = a.item_cd inner join tb_client_mst d on d.client_id = b.client_id inner join tb_users e on e.user_id = b.manager_id where 1=1
and b.contract_date between #{strDate} and #{toDate}
AND a.item_cd LIKE '%' + #{itemCd} + '%'
AND b.item_name LIKE '%' + #{itemName} + '%'
AND e.member_nm LIKE '%' + #{managerName} + '%'
AND d.client_name LIKE '%' + #{clientName} + '%'
AND a.status_Type = #{statusType}
AND a.order_type = #{orderType}
AND a.vat_type = #{vatType}
order by a.po_no desc

mapper.update:
UPDATE tb_contract_mst
SET    attach_file_id = #{attachFileId}
WHERE  contract_id    = #{contractId}

UPDATE tb_contract_mst
SET    expected_due_date   = #{expectedDueDate}
      ,payment_condition   = #{paymentCondition}
      ,manager_id          = #{managerId}
      ,client_id           = #{clientId}
      ,vat_type            = #{vatType}
      ,order_type          = #{orderType}
      ,upd_id              = #{userId}
      ,upd_dt              = GETDATE()
WHERE contract_id = #{contractId}

mapper.insert:
INSERT INTO tb_contract_item (
    contract_item_id,contract_id,po_no,item_cd,qty,unit_price,supply_price,vat_price,degree,status_type,etc,order_dist,reg_id,upd_id
)
VALUES (
        #{contractItemId}
       ,#{contractId}
       ,ISNULL(CAST(#{poNo} AS NVARCHAR(50)),
                 ( SELECT CAST(x.contract_date AS NVARCHAR) + '-' +CAST(x.seq AS NVARCHAR) + '-' +CAST(#{orderDist} AS NVARCHAR)
                     FROM tb_contract_mst x
                     WHERE x.contract_id = #{contractId}
                 )
        )
       ,#{itemCd}
       ,#{qty}
       ,#{unitPrice}
       ,#{supplyPrice}
       ,#{vatPrice}
       ,(SELECT COUNT(*) + 1 FROM tb_contract_item WHERE item_cd = #{itemCd})
       ,#{statusType}
       ,#{etc}
       ,#{orderDist}
       ,#{userId}
       ,#{userId}
       )

mapper.update:
UPDATE tb_contract_item
SET    qty          = #{qty}
      ,unit_price   = #{unitPrice}
      ,supply_price = #{supplyPrice}
      ,vat_price    = #{vatPrice}
      ,degree       = #{degree}
      ,status_type  = #{statusType}
      ,etc          = #{etc}
      ,order_dist   = #{orderDist}
      ,upd_id       = #{userId}
      ,upd_dt       = GETDATE()
WHERE contract_item_id = #{contractItemId}
