mapper.update:
merge into tb_draft_mst a
    using (select #{draftId} as draft_id
                ,#{draftDate} as draft_date
                ,#{seq} as seq
    ) b
    on    a.draft_id = b.draft_id
    and   a.draft_date = b.draft_date
    and   a.seq = b.seq
when matched then
    update  set  client_Name = #{clientName}
                ,item_name = #{itemName}
                ,order_qty = #{orderQty}
                ,due_date = #{dueDate}
                ,attach_file_id = #{attachFileId}
                ,upd_Id = #{userId}
                ,upd_Dt = getdate()
when not matched then
    insert (  draft_id ,draft_date,seq,draft_Dept, draft_user_id,client_Name,item_name,order_qty,due_date,board_id, approval_id
              ,status_Type  ,attach_file_id ,reg_id ,upd_id )
    values ( #{draftId} ,#{draftDate},#{seq},#{draftDept},#{draftUserId},#{clientName},#{itemName}, #{orderQty}, #{dueDate}, #{boardId} , #{approvalId}
            ,#{statusType},#{attachFileId} ,#{userId} ,#{userId} )
;

update tb_draft_mst
set    status_Type = #{statusType}
where  draft_id = #{draftId}

update tb_approval
set    lab_user_id = #{labUserId}
where  approval_id = #{approvalId}

merge into tb_board a
    using (select #{boardId} as board_id
                 ,#{boardUserId} as board_user_id
    ) b
    on    a.board_id = b.board_id
        and   a.board_user_id = b.board_user_id
    when matched then
        update
            set  board_txt = #{boardTxt}
                ,upd_Id = #{userId}
                ,upd_Dt = getdate()
    when not matched then
        insert (  board_id ,board_user_id, board_txt,reg_id ,upd_id )
            values (
                 #{boardId} ,#{boardUserId},#{boardTxt},#{userId} ,#{userId}
                )
;

update tb_approval
set    ${field} = isnull(#{appDate},  convert(nvarchar(10), getdate(), 23))
where  approval_id = #{approvalId}

update tb_draft_mst
set    board_id = #{boardId}
where  draft_id = #{draftId}
