mapper.select:
select b.contract_date +' -'+ CAST(b.seq AS NVARCHAR) AS contractDateSeq ,a.po_no ,a.item_cd ,c.item_name ,b.expected_due_date ,d.client_name ,e.member_nm as managerName ,a.qty ,a.unit_price ,a.supply_price ,a.vat_price ,isnull(a.supply_price, 0)+ isnull(a.vat_price, 0) as totPrice ,dbo.getCommonName('order_type', b.order_type) as orderType ,dbo.getCommonName('status_Type',a.status_type) as statusType ,b.payment_condition ,b.contract_id ,b.contract_date from tb_contract_item a inner join tb_contract_mst b on b.contract_id = a.contract_id inner join tb_item_mst c on c.item_cd = a.item_cd inner join tb_client_mst d on d.client_id = b.client_id inner join tb_users e on e.user_id = b.manager_id where b.contract_date between #{strDate} and #{toDate}
AND a.item_cd LIKE '%' + #{itemCd} + '%'
AND b.item_name LIKE '%' + #{itemName} + '%'
AND e.member_nm LIKE '%' + #{managerName} + '%'
AND d.client_name LIKE '%' + #{clientName} + '%'
AND a.status_Type = #{statusType}
AND a.order_type = #{orderType}
AND a.vat_type = #{vatType}
order by a.po_no desc

mapper.update:
merge into tb_contract_mst a
using   (select #{contractId} as contract_id ) b
on      a.contract_id = b.contract_id
when  matched  then
    update set
             expected_due_date = #{expectedDueDate}
            ,payment_condition =#{paymentCondition}
            ,manager_id=#{managerId}
            ,client_id =#{clientId}
            ,vat_type = #{vatType}
            ,order_type = #{orderType}
            ,upd_id =#{userId}
            ,upd_dt = getdate()
when not matched then
    insert ( contract_id
            ,contract_date
            ,seq
            ,expected_due_date
            ,payment_condition
            ,manager_id
            ,client_id
            ,vat_type
            ,order_type
            ,attach_file_id
            ,reg_id
            ,upd_id )
    values (
            #{contractId}
           ,#{contractDate}
           ,#{seq}
           ,#{expectedDueDate}
           ,#{paymentCondition}
           ,#{managerId}
           ,#{clientId}
           ,#{vatType}
           ,#{orderType}
           ,#{attachFileId}
           ,#{userId}
           ,#{userId}
           )
;

MERGE INTO tb_contract_item a
USING (SELECT #{contractItemId} AS contract_item_id ) b
ON (a.contract_item_id = b.contract_item_id)
WHEN MATCHED THEN
    UPDATE SET
         qty = #{qty}
        ,unit_price = #{unitPrice}
        ,supply_price = #{supplyPrice}
        ,vat_price = #{vatPrice}
        ,order_cnt = #{orderCnt}
        ,status_type = #{statusType}
        ,etc = #{etc}
        ,order_dist = #{orderDist}
        ,upd_id = #{userId}
        ,upd_dt = getDate()
WHEN NOT MATCHED THEN
    INSERT (contract_item_id, contract_id, po_no, item_cd, qty, unit_price, supply_price, vat_price, order_Cnt, status_type ,etc, order_dist, reg_id, upd_id)
    VALUES (
            #{contractItemId}
           ,#{contractId}
           ,isnull(CAST(#{poNo} AS nvarchar(50)), (select CAST( x.contract_date AS nvarchar)+'-'+CAST(x.seq AS NVARCHAR)+'-'+CAST(#{orderDist} AS nvarchar) from tb_contract_mst x where contract_Id = #{contractId}))
           ,#{itemCd}
           ,#{qty}
           ,#{unitPrice}
           ,#{supplyPrice}
           ,#{vatPrice}
           ,(select count(*)+1 from tb_contract_item where item_cd = #{itemCd})
           ,#{statusType}
           ,#{etc}
           ,#{orderDist}
           ,#{userId}
           ,#{userId}
           )
;
